mapper.select:
SELECT a.*,b.nickname as cusname,b.phone as phone, 
c.drivername as drivername,c.number as number,c.phone as driverphone  
FROM orders a 
LEFT JOIN customer b ON a.cus_id=b.id 
LEFT JOIN drivers c ON a.driver=c.id 

	 AND a.ID =#{id} 
	 AND a.CUS_ID =#{cus_id} 
	 AND a.PRICE =#{price} 
	 AND a.DRIVER =#{driver} 
	 AND a.NOTE =#{note} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.PAY_DT) >=unix_timestamp(#{pay_dtFrom}) 
	 AND unix_timestamp(a.PAY_DT) <=unix_timestamp(#{pay_dtTo}) 
	 AND a.PAY_DT =#{pay_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATUS =#{status} 
	 AND a.STATE =#{state} 
	 AND a.ORDERNUM =#{ordernum} 
	 AND CONCAT(IFNULL(a.ORDERNUM,''),' ',IFNULL(b.phone,''),' ',IFNULL(c.phone,'')) like #{searchText} 

	 ORDER BY    ${orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(a.id) 
FROM orders a 
LEFT JOIN customer b ON a.cus_id=b.id 
LEFT JOIN drivers c ON a.driver=c.id 

	 AND a.ID =#{id} 
	 AND a.CUS_ID =#{cus_id} 
	 AND a.PRICE =#{price} 
	 AND a.DRIVER =#{driver} 
	 AND a.NOTE =#{note} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.PAY_DT) >=unix_timestamp(#{pay_dtFrom}) 
	 AND unix_timestamp(a.PAY_DT) <=unix_timestamp(#{pay_dtTo}) 
	 AND a.PAY_DT =#{pay_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATUS =#{status} 
	 AND a.STATE =#{state} 
	 AND a.ORDERNUM =#{ordernum} 
	 AND CONCAT(IFNULL(a.ORDERNUM,''),' ',IFNULL(b.phone,''),' ',IFNULL(c.phone,'')) like #{searchText}

mapper.update:
UPDATE orders
ID=#{id}, CUS_ID=#{cus_id}, PRICE=#{price}, DRIVER=#{driver}, NOTE=#{note}, C_DT=#{c_dt}, PAY_DT=#{pay_dt}, U_DT=#{u_dt}, STATUS=#{status}, STATE=#{state}, ORDERNUM=#{ordernum},
WHERE ID=#{id}
