mapper.select:
		SELECT DATE_FORMAT(c_dt,


                 '%Y年%m月'


                 '%Y年%m月%d日'


                 '%Y年%m月%d日%H时'



		) as days,COUNT(id) as count 
		FROM customer 

			 AND ID =#{id} 
			 AND NICKNAME =#{nickname} 
			 AND PHONE =#{phone} 
			 AND PASSWORD =#{password} 
			 AND HEADER =#{header} 
			 AND WECHAT =#{wechat} 
			 AND INVITER =#{inviter} 
			 AND INVITECODE =#{invitecode} 
			 AND ISNEW =#{isnew} 
			 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
			 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
			 AND C_DT =#{c_dt} 
			 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
			 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
			 AND U_DT =#{u_dt} 
			 AND STATE =#{state} 
			 AND BALANCE =#{balance} 
			 AND PHONE like #{searchText} 

		GROUP BY days

SELECT a.*,b.phone as invitername 
FROM customer a 
LEFT JOIN customer b on a.inviter=b.id 

	 AND a.ID =#{id} 
	 AND a.NICKNAME =#{nickname} 
	 AND a.PHONE =#{phone} 
	 AND a.PASSWORD =#{password} 
	 AND a.HEADER =#{header} 
	 AND a.WECHAT =#{wechat} 
	 AND a.INVITER =#{inviter} 
	 AND a.INVITECODE =#{invitecode} 
	 AND a.ISNEW =#{isnew} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATE =#{state} 
	 AND a.BALANCE =#{balance} 
	 AND a.PHONE like #{searchText} 

	 ORDER BY   #{orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM customer 

	 AND ID =#{id} 
	 AND NICKNAME =#{nickname} 
	 AND PHONE =#{phone} 
	 AND PASSWORD =#{password} 
	 AND HEADER =#{header} 
	 AND WECHAT =#{wechat} 
	 AND INVITER =#{inviter} 
	 AND INVITECODE =#{invitecode} 
	 AND ISNEW =#{isnew} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATE =#{state} 
	 AND BALANCE =#{balance} 
	 AND PHONE like #{searchText}

mapper.update:
UPDATE customer
ID=#{id}, NICKNAME=#{nickname}, PHONE=#{phone}, PASSWORD=#{password}, HEADER=#{header}, WECHAT=#{wechat}, INVITER=#{inviter}, INVITECODE=#{invitecode}, ISNEW=#{isnew}, C_DT=#{c_dt}, U_DT=#{u_dt}, STATE=#{state}, BALANCE=#{balance},
WHERE ID=#{id}
