mapper.select:
SELECT a.*,b.nickname as creater 
FROM goods a 
LEFT JOIN user b  on a.c_id=b.id 

	 AND a.ID =#{id} 
	 AND a.TITLE =#{title} 
	 AND a.IMAGE =#{image} 
	 AND a.BANNERS =#{banners} 
	 AND a.BRAND =#{brand} 
	 AND a.MODEL =#{model} 
	 AND a.OLDLEVEL =#{oldlevel} 
	 AND a.CONFIGURE =#{configure} 
	 AND a.NETWORK =#{network} 
	 AND a.CONTENT =#{content} 
	 AND a.TYPE =#{type} 
	 AND a.SUBTYPE =#{subtype} 
	 AND a.C_ID =#{c_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATE =#{state} 

	 AND CONCAT(IFNULL(a.TITLE,''),' ',IFNULL(a.OLDLEVEL,''),' ',IFNULL(a.CONFIGURE,''),' ',IFNULL(a.NETWORK,'')) like '%${searchtext}%' 


	 ORDER BY   #{orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM goods 

	 AND ID =#{id} 
	 AND TITLE =#{title} 
	 AND IMAGE =#{image} 
	 AND BANNERS =#{banners} 
	 AND BRAND =#{brand} 
	 AND MODEL =#{model} 
	 AND OLDLEVEL =#{oldlevel} 
	 AND CONFIGURE =#{configure} 
	 AND NETWORK =#{network} 
	 AND CONTENT =#{content} 
	 AND TYPE =#{type} 
	 AND SUBTYPE =#{subtype} 
	 AND C_ID =#{c_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATE =#{state}

mapper.update:
UPDATE goods
ID=#{id}, TITLE=#{title}, IMAGE=#{image}, BANNERS=#{banners}, BRAND=#{brand}, MODEL=#{model}, OLDLEVEL=#{oldlevel}, CONFIGURE=#{configure}, NETWORK=#{network}, CONTENT=#{content}, TYPE=#{type}, SUBTYPE=#{subtype}, C_ID=#{c_id}, C_DT=#{c_dt}, U_DT=#{u_dt}, STATE=#{state},
WHERE ID=#{id}
