mapper.select:
SELECT a.*,b.nickname as creater 
FROM news a 
LEFT JOIN user b  on a.c_id=b.id 

	 AND a.ID =#{id} 
	 AND a.TITLE =#{title} 
	 AND a.IMAGE =#{image} 
	 AND a.CONTENT =#{content} 
	 AND a.TYPE =#{type} 
	 AND a.C_ID =#{c_id} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATE =#{state} 

	 ORDER BY   #{orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM news 

	 AND ID =#{id} 
	 AND TITLE =#{title} 
	 AND IMAGE =#{image} 
	 AND CONTENT =#{content} 
	 AND TYPE =#{type} 
	 AND C_ID =#{c_id} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATE =#{state}

mapper.update:
UPDATE news
ID=#{id}, TITLE=#{title}, IMAGE=#{image}, CONTENT=#{content}, TYPE=#{type}, C_ID=#{c_id}, C_DT=#{c_dt}, U_DT=#{u_dt}, STATE=#{state},
WHERE ID=#{id}
