mapper.select:
SELECT a.*,b.nickname as cusname,b.phone as phone 
FROM orderspt a 
LEFT JOIN customer b ON a.cus_id=b.id 

	 AND a.ID =#{id} 
	 AND a.CUS_ID =#{cus_id} 
	 AND a.ORDERNUM =#{ordernum} 
	 AND a.PRICE =#{price} 
	 AND a.FROMADDRESS =#{fromaddress} 
	 AND a.FROMPHONE =#{fromphone} 
	 AND a.TOADDRESS =#{toaddress} 
	 AND a.TOPHONE =#{tophone} 
	 AND a.NOTE =#{note} 
	 AND unix_timestamp(a.SEND_DT) >=unix_timestamp(#{send_dtFrom}) 
	 AND unix_timestamp(a.SEND_DT) <=unix_timestamp(#{send_dtTo}) 
	 AND a.SEND_DT =#{send_dt} 
	 AND unix_timestamp(a.C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(a.C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND a.C_DT =#{c_dt} 
	 AND unix_timestamp(a.U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(a.U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND a.U_DT =#{u_dt} 
	 AND a.STATUS =#{status} 
	 AND a.STATE =#{state} 

	 ORDER BY   #{orderBy} 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM orderspt 

	 AND ID =#{id} 
	 AND CUS_ID =#{cus_id} 
	 AND ORDERNUM =#{ordernum} 
	 AND PRICE =#{price} 
	 AND FROMADDRESS =#{fromaddress} 
	 AND FROMPHONE =#{fromphone} 
	 AND TOADDRESS =#{toaddress} 
	 AND TOPHONE =#{tophone} 
	 AND NOTE =#{note} 
	 AND unix_timestamp(SEND_DT) >=unix_timestamp(#{send_dtFrom}) 
	 AND unix_timestamp(SEND_DT) <=unix_timestamp(#{send_dtTo}) 
	 AND SEND_DT =#{send_dt} 
	 AND unix_timestamp(C_DT) >=unix_timestamp(#{c_dtFrom}) 
	 AND unix_timestamp(C_DT) <=unix_timestamp(#{c_dtTo}) 
	 AND C_DT =#{c_dt} 
	 AND unix_timestamp(U_DT) >=unix_timestamp(#{u_dtFrom}) 
	 AND unix_timestamp(U_DT) <=unix_timestamp(#{u_dtTo}) 
	 AND U_DT =#{u_dt} 
	 AND STATUS =#{status} 
	 AND STATE =#{state}

mapper.update:
UPDATE orderspt
ID=#{id}, CUS_ID=#{cus_id}, ORDERNUM=#{ordernum}, PRICE=#{price}, FROMADDRESS=#{fromaddress}, FROMPHONE=#{fromphone}, TOADDRESS=#{toaddress}, TOPHONE=#{tophone}, NOTE=#{note}, SEND_DT=#{send_dt}, C_DT=#{c_dt}, U_DT=#{u_dt}, STATUS=#{status}, STATE=#{state},
WHERE ID=#{id}
